  #{date,jdbcType=BIGINT}, #{address,jdbcType=VARCHAR}, #{detail,jdbcType=VARCHAR}, 
  #{img,jdbcType=VARCHAR}, #{signUp,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
UPDATE t_activity 
SET sign_up = 
(
	CASE WHEN ISNULL(sign_up) || sign_up='' 
	THEN #{userId,jdbcType=INTEGER} 
	ELSE CONCAT(sign_up,',',#{userId,jdbcType=INTEGER}) 
	END
) WHERE id = #{id,jdbcType=INTEGER};
